import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { listUser  } from "../../actions/userActions";

class List extends Component {
  constructor() {
    super();
    this.state={
      page: '',
      per_page: '',
      total: '',
      total_pages: '',
      data: []
    }
    // this.handleNextClick = this.handleNextClick.bind(this);
    // this.handlePreviousClick = this.handlePreviousClick.bind(this);
    // listUser({page :1 })
  }

  // componentDidMount(){
  //   if(this.state.page=== '')

  // }

  static getDerivedStateFromProps(nextProps,prevState){
    if(nextProps.user.data.page !== ''){
      nextProps.listUser(prevState.page + 1 )
      // nextProps.listUser(nextProps.data.page);
      return nextProps.user.data
    }
    else{
      nextProps.listUser(1);
      return {
        page: prevState.page,
        per_page: prevState.per_page,
        total: prevState.total,
        total_pages: prevState.tot,
        data: []
      }
    }
      // console.log
      // this.setState(nextProps.user);

  }

  handleNextClick() {
    this.setState((prevState)=>({
      page : prevState.page+1
    }));
  }
