import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { listUser  } from "../../actions/userActions";

class List extends Component {
  constructor() {
    super();
    this.state= {
      page: '',
      per_page: '',
      total: '',
      total_pages: '',
      data: []
  }
    // this.handleNextClick = this.handleNextClick.bind(this);
    // this.handlePreviousClick = this.handlePreviousClick.bind(this);
    // listUser({page :1 })
  }

  componentDidMount(){
    if(this.state.page=== '')
      this.props.listUser(1)
    // this.props.listUser(parseInt(this.state.user.page));    
  }

  static getDerivedStateFromProps(nextProps,prevState){
    if(nextProps.user.data.page !== '' ){
      return nextProps.user;
    }  
  }

  handleNextClick(page) {

    this.setState({'page' : page + 1 },function() {
      this.props.listUser(page+1);
    })

    // this.props.listUser(this.st);   
    // this.props.listUser(page);
  }
  handlePreviousClick(e) {

  }
  render() {
    // const { errors } = thi s.state;

    return (
            <div className="register">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <h1 className="display-4 text-center">List Users</h1>
              <p className="lead text-center">
                Watch list of user
              </p>
              <form onSubmit={this.onSubmit}>
                <table className="table">
                  <thead>
                    <tr>
                      <th scope="col">ID</th>
                      <th scope="col">First Name</th>
                      <th scope="col">Last Name</th>
                      <th scope="col">Avatar</th>
                    </tr>
                  </thead>
                  <tbody>
                    {/* {    alert(JSON.stringify(this.state.data))} */}
                    {/* {this.props.user.data.data ? 
                      this.props.user.data.data.map((ele,i) => <tr key= {i}> 
                        <th key={ele.id}>{ele.id}</th>
                        <td key={ele.first_name}>{ele.first_name}</td>
                        <td key={ele.last_name}>{ele.last_name}</td>
                        <td key={ele.avatar}> {ele.avatar}</td>
                      </tr>): null }  */} 
                       {this.state.data.length > 0 ? 
                      this.state.data.map((ele,i) => <tr key= {i}> 
                        <th key={ele.id}>{ele.id}</th>
                        <td key={ele.first_name}>{ele.first_name}</td>
                        <td key={ele.last_name}>{ele.last_name}</td>
                        <td key={ele.avatar}> {ele.avatar}</td>
                      </tr>): null }  
                  </tbody>
                </table>
                {/* <label>{JSON.stringify(this.state.user.page)}</label> */}

                <button onClick={this.handleNextClick.bind(this,this.state.page)} disabled={ parseInt(this.state.page) === parseInt(this.state.total_pages)  ? 'disabled':'' } >Next</button> 
                {/* <button onClick={this.handlePreviousClick} disabled={parseInt(this.state.page)=== 1 ? 'disabled':''}>Prev</button> */}
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
List.propTypes = {
  listUser: PropTypes.func.isRequired,
  user: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};
const mapStateToProps = state => {
 return{
  user: state.user,
  errors: state.errors
 }
};
export default connect(
  mapStateToProps,
  { listUser }
)(List);
