import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { deleteUser } from "../../actions/userActions";

class Delete extends Component {
  constructor() {
    super();
    this.state = {
      errors: {}
    };
    this.onSubmit = this.onSubmit.bind(this);
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    console.log(nextProps);
    if (Object.keys(nextProps.errors).length > 0) {
      return { errors: nextProps.errors };
    }
    else if (nextProps.user.data) {
        alert('hi');
      return nextProps.user.data;
    }
    return null;
  }
  onSubmit(e) {
    e.preventDefault();
    this.props.deleteUser();
  }

  render() {
    let success;

    if (this.state ===  '') {
      success = <div class="alert alert-success alert-dismissible" styles="{{position:'fixed',top : 10px}}">
      <a href="#" class="close" data-dismiss="alert" aria-label="close">&times;</a>
      Delete successful on <strong>{new Date().toDateString()}</strong> At 
       &nbsp;<strong>{new Date().getHours() } : {new Date().getMinutes() } </strong>
      </div>
    }     
    return (
      <div className="register">
        <div className="container">
        {success}{JSON.stringify(this.state)}
          <div className="row">
            <div className="col-md-8 m-auto">
              <h1 className="display-4 text-center">Delete</h1>
              <p className="lead text-center">Delete the Values</p>
              <form onSubmit={this.onSubmit}>
                <input type="submit" value="Delete" className="btn btn-info btn-block mt-4" />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
Delete.propTypes = {
  deleteUser: PropTypes.func.isRequired,
  user: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  user: state.user,
  errors: state.errors
});
export default connect(
  mapStateToProps,
  { deleteUser }
)(Delete);
