import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { deleteUser } from "../../actions/userActions";

class Delete extends Component {
  constructor() {
    super();
    this.state = {
      errors: {}
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    console.log(nextProps);
    if (Object.keys(nextProps.errors).length > 0) {
      return { errors: nextProps.errors };
    }
    else if (nextProps.user.data) {
      return nextProps.user.data;
    }

    return null;
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }
  onSubmit(e) {
    e.preventDefault();
    this.props.deleteUser();
  }

  render() {
    // const { errors } = this.state;
    return (
      <div className="register">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <h1 className="display-4 text-center">Delete</h1>
              <p className="lead text-center">Delete the Values</p>
              <form onSubmit={this.onSubmit}>
                <input type="submit" value="Delete" className="btn btn-info btn-block mt-4" />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
Delete.propTypes = {
  deleteUser: PropTypes.func.isRequired,
  user: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  user: state.user,
  errors: state.errors
});
export default connect(
  mapStateToProps,
  { deleteUser }
)(Delete);
