import React, { Component } from "react";
import PropTypes from "prop-types";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { registerUser } from "../../actions/userActions";
import TextFieldGroup from "../common/TextFieldGroup";

class Register extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      job: "",
      errors: {}
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }
  componentDidMount() {

  }
  static getDerivedStateFromProps(nextProps, prevState) {
    console.log(nextProps);
    if (Object.keys(nextProps.errors).length > 0) {
      alert('hi')
      return { errors: nextProps.errors };
    }
    else if (nextProps.user.data) {
      return nextProps.user.data;
    }

    return null;
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }
  onSubmit(e) {
    e.preventDefault();
    const newUser = {
      name: this.state.name,
      job: this.state.job
    };
    this.props.registerUser(newUser);
  }

  render() {
    const { errors } = this.state;
    const ID = this.state.id;
    let success;
    if (ID) {
      success = <div class="alert alert-success alert-dismissible" styles="{{position:'fixed',top : 10px}}">
      <a href="#" class="close" data-dismiss="alert" aria-label="close">&times;</a>
      Registeration successful with Id: <strong>{this.state.id}</strong> on <strong>{new Date(this.state.createdAt).toDateString()}</strong>
      <strong>{new Date(this.state.createdAt).toTimeString()}</strong>
      </div>
    } 
    return (
      <div className="register">
        <div className="container">
        {success}
          <div className="row">
            <div className="col-md-8 m-auto">
              <h1 className="display-4 text-center">Sign Up</h1>
              <p className="lead text-center">Insert the Values</p>
              <form noValidate onSubmit={this.onSubmit}>
                <TextFieldGroup
                  type="text"
                  placeholder="Enter Name"
                  name="name"
                  value={this.state.name}
                  onChange={this.onChange}
                  error={errors.name}
                />
                <TextFieldGroup
                  type="text"
                  placeholder="Enter Job"
                  name="job"
                  value={this.state.job}
                  onChange={this.onChange}
                  error={errors.job}
                />

                <input type="submit" className="btn btn-info btn-block mt-4" />

              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
Register.propTypes = {
  registerUser: PropTypes.func.isRequired,
  user: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};
const mapStateToProps = state => {
  return {
    user: state.user,
    errors: state.errors
  };
};
export default connect(
  mapStateToProps,
  { registerUser }
)(withRouter(Register));
